import './App.css';
import React, { useState } from 'react';

const App = () => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Велосипед', count: 1 },
    { id: 2, name: 'Самокат', count: 1 },
    { id: 3, name: 'Ролики', count: 2 },
    { id: 4, name: 'Сноуборд', count: 4 },
  ]);

  const addProduct = () => {
    const input = prompt('Введите имя и цену товара (например, "Велосипед 1000")');
    if (input) {
      const [name] = input.split(' ');
      const newProduct = {
        id: Date.now(),
        name: name,
        count: 1,
      };
      setProducts([...products, newProduct]);
    }
  };

  const incrementCount = (id) => {
    setProducts(products.map(product =>
      product.id === id && product.count < 25 ? { ...product, count: product.count + 1 } : product
    ));
  };

  const decrementCount = (id) => {
    setProducts(products.map(product =>
      product.id === id && product.count > 0 ? { ...product, count: product.count - 1 } : product
    ));
  };

  const removeProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  const handleDoubleClick = (id) => {
    const product = products.find(p => p.id === id);
    if (product && product.count === 0) {
      removeProduct(id);
    }
  };

  return (
    <div>
      <button class="add_product_button" onClick={addProduct}>Добавить товар</button>
      <ul class="position">
        {products.map(product => (
          <li key={product.id} onDoubleClick={() => handleDoubleClick(product.id)}>
			<div class="box">
            {product.name} - Количество: {product.count}
            <button onClick={() => incrementCount(product.id)}>+</button>
            <button onClick={() => decrementCount(product.id)}>-</button>
			</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
